package ru.tds.manyfiles; public class ProgrammWithoutCommentsAndExtraSpaces { public static void main(String[] args) throws IOException { DataOutputStream writer = new DataOutputStream(new FileOutputStream(new File("int6data.dat"))); DataInputStream reader = new DataInputStream(new FileInputStream(new File("intdata.dat"))); while (reader.available() > 0) { int number = reader.readInt(); if (isCheck(number)) { writer.writeInt(number); } } reader.close(); writer.close(); } private static boolean isCheck(int number) { return number < 1000000 && number > 999; }}